import numpy as np
def predict_house_price(model, bath, balcony, area,size,area_type, availability, location, fsc):
  x = np.zeros(156)
  x[0] = size
  x[1] = area
  x[2] = bath
  x[3] = balcony
  if availability == 'Ready To Move':
    x[columns.index('availability_Ready To Move')] = 1
  area_type = str(area_type)
  
  #print(type(area_type))
  if area_type == 'Plot Area':
  	#print('p')
  	x[6] = 1
  elif area_type == 'Super built-up Area':
  	#print('s')
  	x[4] = 1
  elif area_type == 'Built-up Area':
  	#print('b')
  	x[5] = 1
  if 'location_'+location in columns:
    x[columns.index('location_'+location)] = 1
    #print(location)
  #print(x)
  
  x = fsc.transform([x])
  #print(x)
  return model.predict(x)[0]

